var model = require("./model/listModel.js");
var list =
{
	"index" : function(req,res)
	{
		var Model = new model(req,res);
                Model.index();
                return Model;
	},
	"create" : function(req,res)
	{
		var Model = new model(req,res);
                Model.create();
                return Model;
	},
        "edit"  : function(req,res){
                var Model = new model(req,res);
                Model.edit();
                return Model;
        },
	"allowedMethods" : {"index":true,"create":true,"edit":false}
};
module.exports = list;




